package world;public class Force {	public static final float FRICTION = -0.3f;    public Force1D x = null;    public Force1D y = null;	public Force() {        x = new Force1D();        y = new Force1D();    }	public boolean hasPush() {		return x.hasPush() || y.hasPush();	}	public void removePush() {		x.removePush();		y.removePush();	}	public String desc() {		return y.desc();	} public void setFriction() {	 x.setSpeed(FRICTION);	 y.setSpeed(FRICTION); }  public Force(float length, double orientation, float speedComp ) {		float xf = length * IntCos.getSin(orientation)/10f;		float yf = - length * IntCos.getCos(orientation)/10f;		x = new Force1D();		x.setConst(xf);		x.setSpeed(speedComp);		y = new Force1D();		y.setConst(yf);		y.setSpeed(speedComp);  } public void setXParams(float _x, float _v, float _c) {     x.setParams(_x, _v, _c); }public void setYParams(float _x, float _v, float _c) {    y.setParams(_x, _v, _c);} public float getXComponent(PosSet p) {        return(x.getForce(p.x, p.vx) ); } public float getYComponent(PosSet p) {        return(y.getForce(p.y, p.vy) ); }}